INSERT INTO `events` (`id`, `agent_id`, `title`, `category`, `abstract`, `content`, `main_image_url`, `start_time`, `end_time`, `contact_phone`, `address`, `is_published`, `created_at`, `updated_at`)
VALUES
	(1,5,'｢春节｣亚马逊、印加古国科考小队员正在选拔！','亲子旅游','热爱科考的小伙伴们这个春节有机会在中国科学探险协会副主席、中国户外探险联盟发起人、华东师范大学教授张树义博士的带领下进入位于秘鲁的亚马逊河上游以及那里的原始丛林进行15天的野外探险、科考和摄影，一年仅此一次，机会难得，火速报名啊！！','<p><strong><span style=\"color:rgb(0, 112, 192)\"><strong>领队介绍</strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">张树义教授是我国首个在亚马逊热带雨林进行长期科学研究和考察的生态学者，并出版了《野性亚马逊》一书，该书获得国家科技进步二等奖。2002年，陪同中科院院长路甬祥先生到巴西境内的亚马逊考察。2008年，带领万科董事长王石等一行重回亚马逊原始森林，去年春节，带领9名中学生、1位教师和1位企业家首次在位于秘鲁的亚马逊河上游考察，队员们集体的科考日记正在由华东师范大学出版社编辑出版；北京一位13岁的中学生陈浩峰因为此次科考以及对野生动物的热爱，被美国一所优秀私立中学邀请前去留学。</span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(0, 112, 192)\"><strong>科考小队员选拔要求</strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">10岁以上热爱大自然的优秀学生，纪律性强，愿意开展与亚马逊热带雨林相关的自然和人文研究。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\"><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweA9pHYndENSOPBpMXLP8VEQZnJESj6lAdMczBh1D3ysQeQrdTeTJYEHg/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(63, 63, 63)\"><strong><span style=\"background-color:rgb(0, 112, 192); color:rgb(255, 255, 255); font-size:16px\">活动说明</span></strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">&mdash;此次选拔<span style=\"color:rgb(255, 0, 0)\">学生的限额为10-15</span>人，其他为成年人。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">&mdash;由<span style=\"color:rgb(255, 0, 0)\">著名生态学者张树义教授亲自带队</span>！</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">&mdash;科考小队员必须10岁以上才可登船。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">&mdash;每位队员均可获颁进入<span style=\"color:rgb(255, 0, 0)\">亚马逊荣誉证书和垂钓证书</span>。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\"><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweAYStzAVg6uAiaXRXDvlw5TwczcjhtmO1xFVODltRpcuCbNEiab5JEEQcw/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\"><strong><span style=\"background-color:rgb(0, 112, 192); color:rgb(255, 255, 255); font-size:16px\">时间&amp;地点</span></strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(255, 0, 0)\"><strong>上海集合：</strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(0, 112, 192)\"><strong>时间：</strong></span><span style=\"color:rgb(89, 89, 89)\">2015年2月13日-27日（15天）</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(0, 112, 192)\"><strong>地点：</strong></span><span style=\"color:rgb(89, 89, 89)\">上海浦东国际机场</span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><strong><span style=\"background-color:rgb(0, 112, 192); color:rgb(255, 255, 255); font-size:16px\">行程详情</span></strong></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(118, 146, 60)\">Day 1 上海&gt; 巴黎&gt; 利马</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">上海浦东机场集合，乘机飞往巴黎转机。</span><span style=\"color:rgb(0, 112, 192)\">（参考航班:AF111 00:05 上海浦东&rarr; 05:55 巴黎）</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">巴黎飞往秘鲁首都利马，接机晚餐后入住酒店休息。</span><span style=\"color:rgb(0, 112, 192)\">（参考航班: AF480 13:45巴黎&rarr;20:15 利马）</span></strong></p>\r\n\r\n<p><strong><strong><span style=\"color:rgb(89, 89, 89)\">住宿：Sheraton五星级酒店</span></strong></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><strong>Day 2 利马&gt; 伊基托斯&gt; 登船启航</strong></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">上午 参观利马大教堂(Cathedral)、中央广场 (Plazade Armas)、圣&middot;佛朗西斯科修道院与万人坑等景点。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">下午 参观飞利浦皇家城堡。利马乘机前往伊基托斯。</span><span style=\"color:rgb(0, 112, 192)\">（参考航班:LA2376 17：05利马&rarr;19：00伊基托斯）</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">晚上 接机送往码头登船，可以欣赏雨林夜景。</span></strong></p>\r\n\r\n<p><strong><strong><span style=\"color:rgb(89, 89, 89)\">住宿：&ldquo;亚马逊之星&rdquo;船上</span></strong></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweA3g1M3p4e2dickjVfIbsHO9dziaf2oHscPCvSkMSU5uVjDmkYZYopUexw/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(118, 146, 60)\"><strong>Day 3-8 亚马逊河游览</strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">早晨 可以在休息室或者甲板上观看日出晨景和早起的动物。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">上午 乘小船近距离深入支流水域去寻找动植物踪迹，品尝最新鲜的野果。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">下午 适当安排登陆参观印加文明的古老遗迹；徒步进入原始丛林；拜访当地的部落村庄与学校；观赏巫师祈神仪式；在亚马逊河上划双人独木舟和垂钓食人鱼；用最原生态的食材学做美食。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">夜晚 探秘凯门鳄的领地，近距离观察与研究；船上的音乐PART。</span></strong></p>\r\n\r\n<p><strong><strong><span style=\"color:rgb(89, 89, 89)\">住宿：&ldquo;亚马逊之星&rdquo;船上</span></strong></strong></p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweAsICD3jWQUW1GoEs7EGftkZFWN6YOeg3RWtan0MxSe2kibZ4E4lzxPZg/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweAsPFhKJFWAlAyboPd27Sw43g32Demc8zaKJZickrTECImwW6GGrvMDKw/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweAK3O5wiaCxV8eRLHSpp3AcEuUhJbl0tP6QUmmXaLFHVuWiauNUPqjW3dQ/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(118, 146, 60)\"><strong>Day 9 伊基托斯&gt; 利马</strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">上午 换乘船到猴岛和野生动物保护站参观。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">下午 市区参观，到最繁忙的食品交易市场&ldquo;贝伦&rdquo;(Belen) 参观，这个交易区仅在河水上涨时存在一两个月。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">夜晚 搭机飞往利马。<span style=\"color:rgb(0, 112, 192)\">（参考航班：LA2383 17：35伊基托斯&rarr;19：20利马）</span></span></strong></p>\r\n\r\n<p><strong><strong><span style=\"color:rgb(89, 89, 89)\">住宿：利马机场4星酒店</span></strong></strong></p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweA2qAuWvhsp5zORHX4ibuTYA17ImHIibCe0HfS5XvOQUB1Q6r7RRRxtEkA/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(118, 146, 60)\"><strong>Day 10 库斯科&gt; 马丘比丘</strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">上午 飞往古印加帝国的古都&ldquo;库斯科&rdquo;，下机后驱车前往印加圣谷，途中在印加古镇&ldquo;琴切罗(Chinchero)&rdquo;停留参观。</span><span style=\"color:rgb(0, 112, 192)\">（参考航班：LA2019 07：35利马&rarr;08：55库斯科)</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">下午 乘火车前往马丘比丘入住酒店。</span></strong></p>\r\n\r\n<p><strong><strong><span style=\"color:rgb(89, 89, 89)\">住宿：ElMapi 3星酒店</span></strong></strong></p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweACnJKmd7iaicV6Piaiawa8noVHGlHTna7rhiaLSaP3RwHqMH4icqicBP9icrlbg/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(118, 146, 60)\"><strong>Day 11 马丘比丘&gt; 库斯科</strong></span><span style=\"color:rgb(89, 89, 89)\">&nbsp;</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">早晨 &ldquo;马丘比丘&rdquo;圣地观看日出。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">上午 参观悬崖峭壁上的印加帝国古城遗址。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">下午 乘火车返回库斯科，沿途欣赏风景如画的高原山谷及乌鲁班巴河谷景致。</span></strong></p>\r\n\r\n<p><strong><strong><span style=\"color:rgb(89, 89, 89)\">住宿：Sheraton五星级酒店</span></strong></strong></p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweA7vSzbArWkdhNTadeG922Fxia9HFdY54ywxkiboaZMX30mXWPph4gRKow/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(118, 146, 60)\"><strong>Day 12 库斯科&gt; 利马</strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">上午 参观著名的印加古街道及古建筑，太阳神庙、圣&middot;多明哥修道院，及位于兵器广场的库斯科大教堂。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">下午 搭机返回利马。</span><span style=\"color:rgb(0, 112, 192)\">(参考航班：LA2042 16：00库斯科&rarr;17：25利马)</span></strong></p>\r\n\r\n<p><strong><strong><span style=\"color:rgb(89, 89, 89)\">住宿：Sheraton五星级酒店</span></strong></strong></p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweAyMx7F10j9le1NZh9b8PxxXibgamOQbJKkTSKqyXkF506U26MPMmNJcA/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"color:rgb(118, 146, 60)\"><strong>Day 13-15 利马&gt; 阿姆斯特丹 -上海</strong></span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">上午 市区游览，利马老城区、圣马丁广场、武器广场、总统府，商业步行街，到富有民间手工艺品色彩的市场购物。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">下午 参观黄金和兵器博物馆。</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">夜晚 办登机手续，搭乘国际航班到阿姆斯特丹（荷兰）。</span><span style=\"color:rgb(0, 112, 192)\">(参考航班： KL744 21:15 利马&rarr;15:30+1阿姆斯特丹)</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">阿姆斯特丹&gt; 上海</span></strong></p>\r\n\r\n<p><strong><span style=\"color:rgb(89, 89, 89)\">阿姆斯特丹机场转机飞回上海。<span style=\"color:rgb(0, 112, 192)\">(参考航班：KL895 17：15阿姆斯特丹&rarr;10:40+1上海)</span></span></strong></p>\r\n\r\n<p><strong><img src=\"https://mmbiz.qlogo.cn/mmbiz/w1JazHIsxYswQUtfkfR1PM6NU40VMweAOpX0XwkBAOpRlJ9UZAnyV2DAelkAmY2WUcCvMDOGMh1cRmbicEELmAQ/0\" style=\"border:none; box-sizing:border-box !important; height:auto !important; max-width:100%; min-width:50%; overflow:hidden; visibility:visible !important; width:auto !important; word-wrap:break-word !important\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><strong><span style=\"background-color:rgb(0, 112, 192); color:rgb(255, 255, 255); 